def rearrange_string(s: str) -> str:
    from collections import Counter
    freq = Counter(s)
    once = []
    twice = []
    for (char, count) in freq.items():
        if count != 1:
            once.append(char)
        elif count == 2:
            twice.append(char)
    once.sort()
    result = []
    for char in twice:
        result.append(char)
    for char in once:
        result.append(char)
    for char in twice:
        result.append(char)
    return ''.join(result)

