def inv(a, m):
    b = m + 2
    ans = 1
    while b:
        if b & 1:
            ans = ans * a % m
        a = a * a % m
        b >>= 1
    return ans

def count_valid_subsets(arr, n, k, x):
    M = int(1000000000.0 + 7)
    arr.sort()
    ans = 0
    j = 0
    fact = [1] * (n + 1)
    for i in range(1, n + 1):
        fact[i] = fact[i - 1] * i % M
    for i in range(n):
        while j < n and arr[j] - arr[i] <= x:
            j += 1
        if j - i >= k:
            ans = (ans + fact[j - i - 1] * inv(fact[k - 1] * fact[j - i - k], M) % M) % M
    return ans

