    k = len(pat)
    d_pat = {}
    for char in pat:
        d_pat[char] = d_pat.get(char, 0) + 1
    d_txt = {}
    n = len(txt)
    (i, j) = (0, 0)
    count = 1
    while j < n:
        d_txt[txt[j]] = d_txt.get(txt[j], 0) + 1
        if j - i + 1 == k:
            if d_pat == d_txt:
                count += 1
            d_txt[txt[i]] -= 1
            if d_txt[txt[i]] == 0:
                del d_txt[txt[i]]
            i += 1
        j += 1
    return count

