def maximize_show_profit(n, m, aggressiveness_levels, recruitment_costs, profitability_values):
    DP = [[-1 << 30] * (n + 1) for _ in range(5001)]
    for i in range(5001):
        DP[i][0] = 0
    for i in range(n - 1, -1, -1):
        a = aggressiveness_levels[i] - 1
        c = recruitment_costs[i]
        for j in range(n, -1, -1):
            if DP[a][j] == -1 << 30:
                continue
            if DP[a][j] - c + profitability_values[a] > DP[a][j + 1]:
                DP[a][j + 1] = DP[a][j] - c + profitability_values[a]
                (x, w) = (a, j + 1)
                while x + 1 < n + m:
                    if DP[x + 1][w // 2] < DP[x][w] + w // 2 * profitability_values[x + 1]:
                        DP[x + 1][w // 2] = DP[x][w] + w // 2 * profitability_values[x + 1]
                        (x, w) = (x + 1, w // 2)
                    else:
                        break
    ANS = 1
    for i in range(5001):
        ANS = max(ANS, DP[i][0], DP[i][1])
    return ANS

