    def gcd(a, b):
        if a == 0:
            return b
        return gcd(b % a, a)
    if k == 1:
        return (n + 1) * (n + 2) // 2 - n - 1
    a = [gcd(x, k) for x in a]
    s = 0
    e = 0
    total = (n + 1) * (n + 2) // 2 - 1 - n
    c = 1
    while e < n:
        flag = False
        while c % k != 0 and e < n:
            total -= e - s
            c *= a[e]
            e += 1
        while c % k == 0 and s < e:
            c //= a[s]
            s += 1
    total += e - s
    return total

