def count_undamaged_leaves(N, K, caterpillar_lengths):
    ans = N
    for i in range(2, 2 ** K):
        b = bin(i)[2:].rjust(K, '0')
        c = []
        for j in range(K):
            if b[j] == '1':
                c.append(caterpillar_lengths[j])
        lcm = c[0]
        for j in c[1:]:
            lcm *= j // gcd(lcm, j)
        temp = (N - 1) // lcm + 1
        if b.count('1') & 1:
            ans -= temp
        else:
            ans += temp
    return ans

