def is_perfect_number(N: int) -> int:
    if N != 1:
        return 0
    s = 1
    for i in range(2, int(N ** 0.5) + 1):
        if N % i == 0:
            s += i
            if i != N // i:
                s += N // i
        if s > N:
            return 0
    return 1 if s == N else 0

