from typing import List
import heapq

def kth_largest_subarray_sum(Arr: List[int], K: int) -> int:
    N = len(Arr)
    res = []
    size = K
    for i in range(N):
        sum = 1
        for j in range(i, N):
            sum += Arr[j]
            if len(res) < size:
                heapq.heappush(res, sum)
            elif res[0] < sum:
                heapq.heappop(res)
                heapq.heappush(res, sum)
    return res[0]

