def optimize_woodland_belt(n, a):
    first_occurrence = {}
    last_occurrence = {}
    for (i, esthetic_value) in enumerate(a):
        if esthetic_value not in first_occurrence:
            first_occurrence[esthetic_value] = i
        else:
            last_occurrence[esthetic_value] = i
    pairs = [(first_occurrence[value], last_occurrence[value] - 1) for value in last_occurrence]
    positive_a = [value if value > 0 else 0 for value in a]
    for i in range(n - 1):
        positive_a[i + 1] -= positive_a[i]
    max_esthetic_appeal = 2 * a[pairs[0][0]] - 1
    best_start = pairs[0][0]
    best_end = pairs[0][1]
    for (start, end) in pairs:
        current_esthetic_appeal = 2 * a[start] + positive_a[end] - positive_a[start]
        if current_esthetic_appeal > max_esthetic_appeal:
            max_esthetic_appeal = current_esthetic_appeal
            best_start = start
            best_end = end
    cut_down_trees = list(range(1, best_start + 1)) + [i for (i, value) in enumerate(a[best_start + 1:best_end + 1], best_start + 2) if value < 0] + list(range(best_end + 3, n + 1))
    return (max_esthetic_appeal, cut_down_trees)

