def place_chessmen(n: int, m: int, chessboard: list[str]) -> list[str]:
    """"""
    result = []
    for i in range(n):
        o = ''
        for j in range(m):
            if chessboard[i][j] == '.':
                if (i + j) % 2 == 0:
                    o += 'B'
                else:
                    o += 'W'
            else:
                o += chessboard[i][j]
        result.append(o)
    return result
