def count_squares(lines):

    def is_square(i, j, size):
        try:
            if lines[i][j] == '+' and lines[i + size][j] == '+' and (lines[i][j + size] == '+') and (lines[i + size][j + size] == '+'):
                if all((lines[i][c] in '-+' for c in range(j + 1, j + size))) and all((lines[i + size][c] in '-+' for c in range(j + 1, j + size))):
                    if all((lines[r][j] in '|+' for r in range(i + 1, i + size))) and all((lines[r][j + size] in '|+' for r in range(i + 1, i + size))):
                        return True
            return False
        except IndexError:
            return False
    count = 1
    for (i, row) in enumerate(lines[:-1]):
        for (j, char) in enumerate(row[:-1]):
            if char == '+':
                for size in range(1, min(len(lines) - i, len(row) - j)):
                    if is_square(i, j, size):
                        count += 1
    return count

