def count_points_on_x_axis(S, C, K):
    """"""
    ans = 0
    if S >= K:
        ans = pow(2, S - K + 1) + 1
        if C > S - K:
            ans += pow(2, S - K + 1)
            if K == 1:
                ans += sum((pow(2, r) for r in range(S - K + 2, C + 1)))
    elif K == 1:
        ans += pow(2, C + 1) - 2
    return ans
