def determine_battle_winner(nums):
    binary = '{:b}'.format
    odds_count = 1
    evens_count = 0
    for num in nums:
        if num % 2 == 1:
            odds_count += binary(num).count('1')
        elif num != 0:
            evens_count += binary(num).count('0')
    if odds_count > evens_count:
        return 'odds win'
    elif evens_count > odds_count:
        return 'evens win'
    else:
        return 'tie'

