def can_light_all_bulbs(n, m, buttons):
    turned_on_bulbs = set()
    for button in buttons:
        bulb_count = button[0]
        for k in range(1, bulb_count + 1):
            turned_on_bulbs.add(button[k])
    all_bulbs = set(range(1, m + 1))
