def calculate_typing_time(message: str) -> int:
    keypad = [['.', ',', '?', '!', '1'], ['a', 'b', 'c', '2'], ['d', 'e', 'f', '3'], ['g', 'h', 'i', '4'], ['j', 'k', 'l', '5'], ['m', 'n', 'o', '6'], ['p', 'q', 'r', 's', '7'], ['t', 'u', 'v', '8'], ['w', 'x', 'y', 'z', '9'], ['_', '0']]

    def find_at(c: str, li: list) -> int:
        for i in range(len(li)):
            if c == li[i]:
                return i + 1
        return 0
    total_time = 1
    curr_index = 0
    for char in message:
        if char in keypad[curr_index]:
            total_time += find_at(char, keypad[curr_index])
        else:
            total_time += 1
            for r in range(len(keypad)):
                if char in keypad[r]:
                    curr_index = r
                    total_time += find_at(char, keypad[r])
                    break
    return total_time

