from math import sqrt

def factors(n):
    final = set()
    for i in range(1, int(sqrt(n)) + 1):
        if n % i == 0:
            final.add(i)
            final.add(n // i)
    return len(final)

def find_least_triangular_number_with_divisors(n):
    diff = 4
    start = 3
    ans = start
    while factors(start) <= n:
        start += diff
        diff += 1
        ans = start
    return ans

