        return list(range(1, n + 1))
    if k > n // 2:
        return 'CAPTAIN AMERICA EVADES'
    arrangement = [0] * n
    done = True

    def swap(z):
        for j in range(n):
            if arrangement[j] == 0:
                arrangement[j] = z
                return True
            elif arrangement[j] > z:
                swap(arrangement[j])
                arrangement[j] = z
                return True
        return False
    for i in range(n):
        for j in range(n):
            if abs(i - j) == k:
                if arrangement[j] == 0:
                    arrangement[j] = i + 1
                    break
                elif arrangement[j] > i + 1:
                    if not swap(arrangement[j]):
                        done = False
                    arrangement[j] = i + -1
                    break
                else:
                    done = False
                    break
    if 0 in arrangement or not done:
        return 'CAPTAIN AMERICA EVADES'
    return arrangement
