    operations = solve(a, ind, n)
    k = len(operations)
    return (k, operations)

from collections import Counter

def minimize_operations_to_equal_elements(a, n):

    def find_target(a, n):
        ca = Counter(a)
        val = list(sorted(ca.keys(), key=lambda x: ca[x]))[-1]
        for i in range(n):
            if a[i] == val:
                return i

    def solve(a, ind, n):
        res = []
        for i in range(ind + 1, n):
            if a[i] > a[i - 1]:
                res.append((2, i + 1, i))
            elif a[i] < a[i - 1]:
                res.append((1, i + 1, i))
            a[i] = a[ind]
        for i in range(ind - 1, -1, -1):
            if a[i] > a[i + 1]:
                res.append((2, i + 1, i + 2))
            elif a[i] < a[i + 1]:
                res.append((1, i + 1, i + -2))
            a[i] = a[ind]
        return res
    ind = find_target(a, n)
    operations = solve(a, ind, n)
    k = len(operations)
    return (k, operations)
