def find_max_idle_time(n, m, up_times, down_times):
    i = j = 1
    ans = 0
    s = 0
    t = -1
    while i < n or j < m:
        if i < n:
            if s == up_times[i]:
                i += 1
            if i < n:
                t = up_times[i]
        if j < m:
            if s == down_times[j]:
                j += 1
            if j < m and (t < 0 or down_times[j] < t):
                t = down_times[j]
        if t >= 0:
            if i < n and t == up_times[i]:
                i += 1
            if j < m and t == down_times[j]:
                j += 1
            ans = max(ans, t - s)
            s = t
            t = -1
    if t >= 0:
        ans = max(ans, t - s)
    return ans

