def encode_and_flip_sum(test_cases):

    def flip_number(num):
        no = list(map(str, str(num)))
        l = len(no) / 2
        for i in range(l):
            a = no[i]
            no[i] = no[-1 - i]
            no[-1 - i] = a
        return int(''.join(no))
    results = []
    for (x, y) in test_cases:
        flipped_sum = flip_number(x) + flip_number(y)
        encoded_result = flip_number(flipped_sum)
        results.append(encoded_result)
    return results

