def calculate_minimum_bytecoins(n, m, voters):
    un = sum((1 for (p, c) in voters if p == 1))
    cn = [(p, c) for (p, c) in voters if p != 1]
    cn.sort(key=lambda x: x[1])
    d = {}
    for (i, (p, c)) in enumerate(cn):
        if p in d:
            d[p] = [(c, i)]
        else:
            d[p].append((c, i))
    min_val = float('inf')
    for i in range(1, n // 2 + 2):
        total = 0
        v = set()
        for x in d:
            if len(d[x]) >= i:
                r = len(d[x]) - (i - 1)
                for j in range(r):
                    total += d[x][j][0]
                    v.add(d[x][j][1])
        for j in range(len(cn)):
            if len(v) + un >= i:
                break
            if j not in v:
                total += cn[j][1]
                v.add(j)
        min_val = min(total, min_val)
    return min_val

