def find_largest_integer_to_buy(A: int, B: int, X: int) -> int:

    def price(N: int) -> int:
        d = math.floor(math.log10(N)) + 1
        return A * N + B * d
    if price(1) >= X:
        return 0
    left = 1
    right = 10 ** 9
    for _ in range(100):
        mid = (left + right) // 2
        if price(mid) <= X:
            left = mid
        else:
            right = mid
    if price(right) <= X:
        return right
    return left

