        return 0
    possible_tiles = [str(i) + j for i in range(1, 10) for j in 'mps']
    for tile in possible_tiles:
        if is_mentsu(tile, b, c) or is_mentsu(a, tile, b) or is_mentsu(a, b, tile):
            return 1
    return 3

