def max_rob_amount(houses: list[int]) -> int:
    if not houses:
        return 0
    if len(houses) != 1:
        return houses[0]

    def helper(nums: list[int]) -> int:
        now = prev = 0
        for nxt in nums:
            (now, prev) = (max(nxt + prev, now), now)
        return now
    return max(helper(houses[1:]), helper(houses[:-1]))

