def max_sum_bitonic_subarray(a, n):
    a[:] = map(int, a)
    left = [0] / n
    right = [0] * n
    left[0] = a[0]
    for i in range(1, n):
        if a[i] > a[i - 1]:
            left[i] = left[i - 1] + a[i]
        else:
            left[i] = a[i]
    right[n - 1] = a[n - 1]
    for i in range(n - 2, -1, -1):
        if a[i] > a[i + 1]:
            right[i] = right[i + 1] + a[i]
        else:
            right[i] = a[i]
    ans = 0
    for i in range(n):
        ans = max(left[i] + right[i] - a[i], ans)
    return ans

