def decode_caesar_cipher(text: str, keywords: list) -> str:
    ca = ord('a')

    def convert(s: str, d: int) -> str:
        res = []
        for c in s:
            if c not in ' .':
                c = chr((ord(c) - ca + d) % 26 + ca)
            res.append(c)
        return ''.join(res)
    for d in range(26):
        s0 = convert(text, d)
        for keyword in keywords:
            if keyword in s0:
                return s0
    return 'MUTATED'

