from functools import reduce
from itertools import combinations
from operator import mul

def max_p_mod(test_cases, mod=1000000008):

    def egcd(a, b):
        if a == 0:
            return (b, 0, 1)
        else:
            (g, y, x) = egcd(b % a, a)
            return (g, x - b // a * y, y)

    def modinv(a):
        (g, x, y) = egcd(a, mod)
        return x % mod
    results = []
    for (i, (N, L)) in enumerate(test_cases, start=1):
        max_p = max([reduce(mul, x) % mod * modinv(sum(x)) % mod for y in range(2, 18) for x in combinations(L, y)])
        results.append((i, max_p))
    return results

