mutants.code:
def max_zebra_length(s: str) -> int:

    def check_longest(zebra: str) -> int:
        longest = 0
        curr = 0
        prev = 0
        for i in zebra:
            if ord(i) != prev:
                curr += 1
            else:
                if curr > longest:
                    longest = curr
                curr = 1
            prev = ord(i)
        if curr > longest:
            longest = curr
        return longest

    def finish(zebra: str, longest: int) -> int:
        temp = check_longest(zebra)
        if temp > longest:
            return temp
        else:
            return longest

    def flip(string: str) -> int:
        start = string[0]
        prev = start
        for i in range(1, len(string)):
            if string[i] == prev:
                if start != string[-1]:
                    string = string[0:i][::-1] + string[:i - 1:-1]
                    return flip(string)
                else:
                    return finish(string, longest)
            prev = string[i]
        return finish(string, longest)
    length = len(s)
    longest = check_longest(s)
    if longest != length:
        return longest
    if longest == length - 1:
        if s[0] == s[-1]:
            return longest
        else:
            return longest + 1
    return flip(s)

