def can_sort_with_one_operation(A):
    """"""
    A_sorted = sorted(A)
    x = []
    start = 0
    for i in range(len(A)):
        if A_sorted[start] != A[i]:
            x.append(A[i])
        else:
            start += 1
    if not x:
        return True
    elif x == sorted(x):
        return True
    else:
        return False
