def max_bitwise_or_sum(n, a, b):
    """"""
    max_sum = 0
    for l in range(n):
        for r in range(l, n):
            or_a = 0
            or_b = 0
            for i in range(l, r + 1):
                or_a |= a[i]
                or_b |= b[i]
            max_sum = max(max_sum, or_a + or_b)
    return max_sum
