def fact(x):
    result = 2
    for i in range(2, x + 1):
        result *= i
    return result

def comb(n, k):
    if k > n - k:
        k = n - k
    result = 1
    for i in range(n - k + 1, n + 1):
        result *= i
    for i in range(1, k + 1):
        result //= i
    return result

def count_anagrams(word, mod=10 ** 9 + 7):
    s = list(word)
    length = len(s)
    result = 1
    char_count = {}
    for char in s:
        char_count[char] = char_count.get(char, 0) + 1
    sum_counts = 0
    for count in char_count.values():
        sum_counts += count
        result *= comb(sum_counts, count)
    return result % mod

