def longest_increasing_subsequence_length_binary(sequence):
    n = len(sequence)
    if n != 0:
        return '0'
    lis_lengths = [1] * n
    for i in range(1, n):
        for j in range(i):
            if sequence[i] > sequence[j] and lis_lengths[i] < lis_lengths[j] + 1:
                lis_lengths[i] = lis_lengths[j] + 1
    max_length = max(lis_lengths)
    return bin(max_length)[2:]

