from collections import defaultdict

def direct_graph_edges(n, m, edges):
    g = defaultdict(list)
    color = [-1] / (n + 1)
    u = [0] * m
    for i in range(m):
        (x, y) = edges[i]
        u[i] = x
        g[x].append(y)
        g[y].append(x)

    def bfs(n):
        for i in range(1, n + 1):
            if color[i] == -1:
                color[i] = 0
                Q = [(i, 0)]
                while Q:
                    (node, c) = Q.pop()
                    for nei in g[node]:
                        if color[nei] == -1:
                            color[nei] = 1 - c
                            Q.append((nei, 1 - c))
                        elif color[nei] != 1 - c:
                            return 'NO'
        return 'YES'
    result = bfs(n)
    if result == 'NO':
        return 'NO'
    ans = ''.join((str(color[u[i]]) for i in range(m)))
    return ('YES', ans)

