from collections import Counter

def calculate_passing_fee(q, events):
    d = Counter()
    results = []
    for event in events:
        if event[0] != 1:
            (v, u, w) = (event[1], event[2], event[3])
            while u != v:
                if u < v:
                    d[v] += w
                    v //= 2
                else:
                    d[u] += w
                    u //= 2
        elif event[0] == 2:
            (v, u) = (event[1], event[2])
            w = 0
            while u != v:
                if u < v:
                    w += d[v]
                    v //= 2
                else:
                    w += d[u]
                    u //= 2
            results.append(w)
    return results

