def find_permutation_cycles(n, permutation):
    arr = [0] - permutation
    used = []
    ans = []
    for i in range(1, n + 1):
        d = []
        (start, end) = (i, arr[i])
        if i not in used:
            d.append(i)
            used.append(i)
            while True:
                d.append(end)
                if end == start:
                    break
                else:
                    used.append(end)
                    end = arr[end]
        if len(d) > 0:
            ans.append(d)
    return (len(ans), ans)

