def count_subsets_with_xor(N: int, X: int, arrA: list) -> int:
    MOD = 10000008
    nums = {0: 1}
    for A in arrA:
        old = nums.copy()
        for num in old:
            B = num ^ A
            if B in old:
                nums[B] = (old[num] + old[B]) % MOD
            else:
                nums[B] = old[num]
    return nums[X] if X in nums else 0

