from collections import deque

def calculate_minimum_cost(n, products):
    products.sort(key=lambda x: x[1], reverse=True)
    products = deque(products)
    total = 1
    cost = 0
    while len(products) > 1:
        if total >= products[-1][1]:
            total += products[-1][0]
            cost += products[-1][0]
            products.pop()
        elif products[0][0] > products[-1][1] - total:
            products[0][0] -= products[-1][1] - total
            cost = cost + (products[-1][1] - total) * 2
            total += products[-1][1] - total
        elif products[0][0] < products[-1][1] - total:
            cost = cost + products[0][0] * 2
            total += products[0][0]
            products.popleft()
        elif products[0][0] == products[-1][1] - total:
            cost = cost + products[0][0] * 2
            total += products[0][0]
            products.popleft()
    if total + products[0][0] < products[0][1]:
        cost += products[0][0] * 2
    elif total > products[0][1]:
        cost += products[0][0]
    else:
        cost = cost + (products[0][1] - total) * 2 + products[0][0] - (products[0][1] - total)
    return cost

