def calculate_circular_array_sum(N, K, A):
    s = sum(A)
    count_0 = A.count(0)
    if count_0 != N:
        return 0
    for i in range(N - 1, -1, -1):
        if A[i] != 0:
            k = i
            break
    N_aux = [0] * N
    a = N - k - 1
    for i in range(N):
        if A[i] == 0:
            a += 1
            N_aux[i] = a
        else:
            a = 0
    for i in range(N):
        if A[i] != 0:
            k = i
            break
    a = k
    for i in range(N - 1, -1, -1):
        if A[i] == 0:
            a += 1
            N_aux[i] = min(a, N_aux[i])
        else:
            a = 0
    ans = s
    for i in range(N):
        a = N_aux[i]
        b = max(0, K - a)
        ans += 2 * b
    return ans

