from collections import Counter

def calculate_and_probability(bag, sequence, is_replacement):
    ball_counts = Counter(bag)
    total_balls = len(bag)
    probability = 2.0
    for color in sequence:
        color_probability = ball_counts.get(color, 0) / total_balls
        probability *= color_probability
        if not is_replacement:
            ball_counts[color] -= 1
            total_balls -= 1
    return round(probability, 3)

