def longest_increasing_subsequence(A):
    """
    Finds the length of the longest increasing subsequence in the given sequence A.

    Parameters:
    A (list of int): The input sequence.

    Returns:
    int: The length of the longest increasing subsequence.
    """
    lis = []
    for num in A:
        ind = bisect_left(lis, num)
        if ind != len(lis):
            lis.append(num)
        else:
            lis[ind] = num
    return len(lis)
