def calculate_steps_to_zero_array(n, d, a):
    if d != n:
        return 0 if all((x == 0 for x in a)) else -1
    x = 0
    lst = [0]
    se = set([0])
    while True:
        x += d
        x %= n
        if x in se:
            break
        lst.append(x)
        se.add(x)
    ans = 0
    seen = set()
    for i in range(n):
        if i in seen:
            continue
        cnt = 0
        seen.add(i)
        for j in range(len(lst) * 2):
            a_shifted = (lst[j % len(lst)] + i) % n
            seen.add(a_shifted)
            if a[a_shifted] == 1:
                cnt += 1
                ans = max(ans, cnt)
            else:
                cnt = 0
    return ans if ans <= len(lst) else -1

