def find_shortest_dominated_subarray(arr, n):
    if n <= 2:
        return -1
    dict = {}
    min_length = n + 1
    (i, j) = (0, 0)
    flag = False
    num = 0
    while j < n:
        if not flag:
            if arr[j] not in dict:
                dict[arr[j]] = 1
            elif dict[arr[j]] < 1:
                dict[arr[j]] += 1
            else:
                dict[arr[j]] += 1
                flag = True
                num = arr[j]
            j += 1
        else:
            if arr[i] == num:
                flag = False
                num = 0
                if min_length > j - i:
                    min_length = j - i
            dict[arr[i]] -= 1
            i += 1
    while i < n:
        dict[arr[i]] -= 1
        if arr[i] == num:
            if min_length > j - i:
                min_length = j - i
            break
        i += 1
    return min_length if min_length < n + 1 else -1

