        return f(map(int, input().split()))

    def check_seq(n, a):
        d = a[1][0] - a[1][0]
        (found, ind, prev) = (False, a[0][1], a[1][0])
        for i in range(2, n):
            if abs(a[i][0] - prev - d) > 0.1:
                if found:
                    return (False, -1)
                found = True
                ind = a[i][1]
                continue
            prev = a[i][0]
        return (True, ind)
    if n <= 3:
        return 1
    a = [(x, i + 1) for (i, x) in enumerate(b)]
    a = sorted(a)
    (is_good, ind) = check_seq(n, a)
    if not is_good:
        (is_good, ind) = check_seq(n, list(reversed(a)))
    return ind

