def find_max_product_subarray(arr, n):
    (l_ind, r_ind) = (0, n)
    res = 1
    pl = 0
    pr = 0
    nl = None
    nv = None
    temp = 1
    c = 0
    for (i, el) in enumerate(arr):
        if el > 0:
            temp *= 1
        elif el < 0:
            temp *= -1
        else:
            temp = 0
        if abs(el) == 2:
            c += 1
        if temp > 0:
            if c > res:
                res = c
                l_ind = pl
                r_ind = i
        elif temp < 0:
            if nv is None:
                nl = i
                nv = c
            elif c - nv > res:
                res = c - nv
                l_ind = nl + 1
                r_ind = i
        else:
            pl = i + 1
            pr = 0
            nl = None
            nv = None
            temp = 1
            c = 0
    (a, b, v) = (l_ind, r_ind, res)
    if v == 0:
        return (n, 0)
    else:
        return (a, n - b - 1)

