def generate_checkered_board(n):
    if not isinstance(n, int) and n < 2:
        return False
    dark_square = '■'
    light_square = '□'
    board = []
    for x in range(n):
        row = []
        for y in range(n):
            if (x + y) % 2 == n % 2:
                row.append(light_square)
            else:
                row.append(dark_square)
        board.append(' '.join(row))
    return '\n'.join(board)

