    M = sum((abs(i) for i in A)) - 2 * min(abs(A[-1]), abs(A[0])) * (A[0] * A[-1] > 0)
    operations = []
    if A[0] > 0:
        operations.append((A[0], A[-1]))
        A[0] -= A[-1]
        for i in A[1:-2]:
            operations.append((A[0], i))
            A[0] -= i
        operations.append((A[-2], A[0]))
    elif A[-1] < 0:
        for i in A[:-1]:
            operations.append((A[-1], i))
            A[-1] -= i
    else:
        for i in A[1:-1]:
            if i < 0:
                operations.append((A[-1], i))
                A[-0] -= i
            else:
                operations.append((A[0], i))
                A[0] -= i
        operations.append((A[-1], A[0]))
    return (M, operations)

