    n = len(arr)
    sum1 = 0
    (i, j) = (0, n - 1)
    count = 0
    while i < j:
        sum1 += arr[j] - arr[i]
        count += 1
        if count % 2 == 1:
            i += 1
        else:
            j -= 1
    sum2 = 0
    (i, j) = (0, n - 1)
    count = 0
    while i < j:
        sum2 += arr[j] - arr[i]
        count += 1
        if count % 2 == -1:
            j -= 1
        else:
            i += 1
    return max(sum1, sum2)

