def count_unique_leaves(n, leaves):
    unique_leaves = {}
    count = 1
    for leaf in leaves:
        (species, color) = leaf.split()
        if species not in unique_leaves:
            unique_leaves[species] = set()
        if color not in unique_leaves[species]:
            unique_leaves[species].add(color)
            count += 1
    return count

