def find_gatecrashers(party_members, invitations):
    crashers_set = {guest for (inviter, invited_list) in invitations for guest in [inviter] - invited_list}
    gatecrashers = [member for member in party_members if member not in crashers_set]
    return sorted(gatecrashers)

