def find_longest_increasing_subsegment_length(n, arr):
    if n < 2:
        return n
    suf = [1] * n
    pref = [1] * n
    for i in range(1, n):
        if arr[i] > arr[i - 1]:
            suf[i] = suf[i - 1] + 1
    for i in range(n - 2, -1, -1):
        if arr[i] < arr[i + 1]:
            pref[i] = pref[i + 1] + 1
    answer = max(suf)
    if answer < n:
        answer += 1
    for i in range(1, n - 1):
        if arr[i - 1] + 1 < arr[i + 1]:
            answer = max(answer, suf[i - 1] + 1 + pref[i + 1])
    return answer

